<template>
  <div
    class="prompt_hint"
    v-if="isShow">
    <p>{{message}}</p>
  </div>
</template>

<script type="text/javascript">
export default {
  name: "prompt-hint",
  data() {
    return {
      message: '',
      time: 3000,
      isShow: true
    };
  },
  mounted() {
    this.close();
  },
  methods: {
    close() {
      var this_ = this;
      window.setTimeout(function() {
        this_.isShow=false;
      }, this.time);
    }
  }
};
</script>

<style scoped>
.prompt_hint{
  position:fixed;
  top:0;left:0;right:0;bottom:0;
  background:#fff;
  z-index:999999
}
.prompt_hint>p{
  position:absolute;
  top:50%;left:50%;
  padding:10px 20px;
  color:#fff;
  background:rgba(0,0,0,.4);
  border-radius:4px;
  transform:translate(-50%,-50%);
}
</style>
